import React, { useState } from 'react';
import logo from './UI/logo.png';
import logout from './UI/logout.png';
import account from './UI/account.png';
import Modal from './UI/modal/modal';
import { Link } from 'react-router-dom';
import { isAuthorized } from '../redux/features/auth/authSlice';
import { useSelector, useDispatch } from 'react-redux';
import { logOut } from '../redux/features/auth/authSlice';

function Header({ setModal }) {
  const isAuth = useSelector(isAuthorized);
  const dispatch = useDispatch();
  const [headerModal, setHeaderModal] = useState(false);

  function confirmLogout() {
    dispatch(logOut());
    setHeaderModal(false);
  }
  return (
    <div className='header'>
      <div className='headerInner'>
        <div className='logo'>
          <Link to='/'>
            <img src={logo} />
          </Link>
        </div>
        <div className='menu'>
          {isAuth ? (
            <div>
              <div className='accountBtn'>
                <img src={account} />
              </div>
              <div className='accountBtn'>
                <img src={logout} onClick={() => setHeaderModal(true)} />
              </div>
            </div>
          ) : (
            <div>
              <button className='hollowBtn' onClick={() => setModal(true)}>
                Login
              </button>
              <Link to='/registration'>
                <button className='filledBtn'>Registration</button>
              </Link>
            </div>
          )}
        </div>
      </div>
      <Modal visible={headerModal} setVisible={setHeaderModal}>
        <div style={{ padding: '20px' }}>
          <p style={{ textAlign: 'center' }}>
            Are you sure you want to log out?
          </p>
          <button className='hollowBtn' onClick={confirmLogout}>
            Log Out
          </button>
          <button className='filledBtn' onClick={() => setHeaderModal(false)}>
            Stay
          </button>
        </div>
      </Modal>
    </div>
  );
}

export default Header;
