import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getHouses } from '../redux/features/houses/houseSlice';
import { isAuthorized } from '../redux/features/auth/authSlice';
import Loading from '../components/UI/loading/loading';
import Filters from '../components/UI/filters/filters';
import HouseCard from '../components/UI/housecard/housecard';

function MainPage() {
  const dispatch = useDispatch();
  const { houses } = useSelector((state) => state.houses);
  const isAuth = useSelector(isAuthorized);
  const [pages, setPages] = useState([]);

  const isLoading = houses.status === 'loading';
  const isError = houses.status === 'error';

  //connecting to a server
  useEffect(() => {
    dispatch(getHouses());
    setPages.push(houses % 2);
  }, [dispatch]);

  return (
    <div className='mainpage'>
      <Filters />
      {isError ? <p>Nothing found. Please try another query.</p> : isLoading ? (
        <Loading />
      ) : (
        <div className='cards'>
          {isAuth ? houses.items.map((house) => (<HouseCard house={house} key={house.title} />
          )) : houses.items.map((house, index) => index < 6 && (<HouseCard house={house} key={house.title} />))}
        </div>
      )}
    </div>
  );
}

export default MainPage;
