import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import server from '../../../utilites/connection';

const URI = '/';

const initialState = {
  houses: {
    items: [],
    status: null,
  },
  houseInfo: {
    title: '',
    price: 0,
    space: 0,
    address: '',
    desc: '',
    houseType: '',
    picture: '',
    googleurl: '',
    status: null,
  },
};

export const getHouses = createAsyncThunk('houses/getHouses', async () => {
  const res = await server.get(URI);
  return res.data;
});

export const getHouseInfo = createAsyncThunk(
  'houses/getHouseInfo',
  async (id) => {
    try {
      const res = await server.get(URI + id);
      return res.data;
    } catch (error) {
      console.log(error);
    }
  }
);

export const houseSlice = createSlice({
  name: 'houses',
  initialState,
  reducers: {},
  extraReducers: {
    [getHouses.pending]: (state) => {
      state.houses.items = [];
      state.houses.status = 'loading';
    },
    [getHouses.fulfilled]: (state, action) => {
      state.houses.items = action.payload;
      state.houses.status = 'complete';
    },
    [getHouses.rejected]: (state) => {
      state.houses.items = [];
      state.houses.status = 'error';
    },
    [getHouseInfo.pending]: (state) => {
      state.houseInfo.status = 'loading';
    },
    [getHouseInfo.fulfilled]: (state, action) => {
      state.houseInfo.title = action.payload;
      state.houseInfo.price = action.payload;
      state.houseInfo.space = action.payload;
      state.houseInfo.address = action.payload;
      state.houseInfo.desc = action.payload;
      state.houseInfo.picture = action.payload;
      state.houseInfo.googleurl = action.payload;
      state.houseInfo.status = 'complete';
    },
    [getHouseInfo.rejected]: (state) => {
      state.houseInfo.status = 'error';
    },
  },
});

export default houseSlice.reducer;
