import axios from 'axios';
import React, { useEffect, useRef, useState } from 'react';
import '../App.css'

function Registration({ users, createUsers }) {

    const [user, setUser] = useState({ name: '', email: '', phone: '', password: '' });
    const [validated, setValidated] = useState(false);
    const [validationErrors, setValidationErrors] = useState([]);
    
    const nameIn = useRef();
    const emailIn = useRef();
    const phoneIn = useRef();
    const passIn = useRef();
    const repIn = useRef();

    function Validation(e) {
        if (e.target.id === 'name' && e.target.value) {
            e.target.className = 'input';
            setUser({ ...user, name: e.target.value })
        } else {
            setValidationErrors([...validationErrors, 'Add your name']); // ДОБАВЬ И УДАЛЕНИЕ ИЗ МАССИВА
            e.target.className = 'error';
        }

        if (e.target.id === 'email' && e.target.value) {
            const onCheck = e.target.value;
            async function checkEmail(onCheck) {
                const res = await axios.get('http://localhost:8000/'); // ВПИШИ ТЕЛО запроса
                if (res === 'ok') {
                    // Если ответ = ОК, тогда запиши значение
                    setUser({ ...user, email: e.target.value })
                } else {
                    setValidationErrors([...validationErrors, 'User with this email already exists'])
                    e.target.className = 'error';
                }
            }
        } else {
            setValidationErrors([...validationErrors, 'Add your email'])
            e.target.className = 'error';
        }
    }

    useEffect(() => {
        // Validation
        if (passIn.current.value !== repIn.current.value) {
            repIn.current.className = 'error';
            passIn.current.className = 'error';
            setValidated(false);
        } if (users.includes(emailIn.current.value)) {
            alert('Такое мыло уже есть')
            setValidated(false);
        } else {
            nameIn.current.className = 'input';
            emailIn.current.className = 'input';
            phoneIn.current.className = 'input';
            passIn.current.className = 'input';
            repIn.current.className = 'input';
            setValidated(true);
        }
        console.log(validated)
    }, [submit])

    function submit(e) {
        e.preventDefault();

        if (validated) {
            const newUser = { ...user };
            createUsers(newUser);
        }
    }
    // ДИМА, ДОБАВЬ В СТРЕЛОЧНУЮ ФУНКЦИЮ ФУНКЦИЮ И ТАМ ПРОВЕРЯЙ, А ЕСЛИ ОК ТО ПУШ ЗНАЧЕНИЕ

    return <div className='container'>
        <h1>Registration</h1>
        <form className='registration'>
            <label htmlFor='name'>Your name</label>
            <input className='input' id='name' type='text' name='name' placeholder='John Doe' onChange={(e) => setUser({...user, name: e.target.value})} ref={nameIn} required />
            <label htmlFor='email'>Your email</label>
            <input className='input' id='email' type='email' name='email' placeholder='email@email.com' onChange={(e) => setUser({...user, email: e.target.value})} ref={emailIn} required />
            <label htmlFor='phone'>Your phone number</label>
            <input className='input' id='phone' type='text' name='phone' placeholder='8 800 5553535' onChange={(e) => setUser({...user, phone: e.target.value})} ref={phoneIn} required />
            <label htmlFor='password'>Password</label>
            <input className='input' id='password' type='password' name='phone' placeholder='Password' minLength={8} onChange={(e) => setUser({...user, password: e.target.value})} ref={passIn} required/>
            <label htmlFor='repeatpass'>Repeat Password</label>
            <input className='input' id='repeatpass' type='password' name='phone' placeholder='Password' minLength={8} ref={repIn} required/>
            <div><button onClick={submit}>Submit Registration</button></div>
        </form>
    </div>
}

export default Registration;
