import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState({
    path: '',
    files: [],
  });
  //connecting to server
  useEffect(() => {
    fetch('http://localhost:8000/')
      .then((res) => res.json())
      .then(
        (result) => {
          setData(result);
        },
        (error) => {}
      );
  }, []);
  return (
    <div>
      <ul>
        {data.files.map((f) => {
          if (f.dir) {
            return <li>{f.name}</li>;
          } else {
            return <li>{f.name}</li>;
          }
        })}
      </ul>
    </div>
  );
}

export default App;
