import React from 'react';
import Loading from '../components/UI/loading/loading';
import connection from '../components/connection';

function MainPage() {
  const [isLoading, setIsLoading] = useState(false); // loading rotation
  const [housedata, setHouseData] = useState([]);

  //connecting to a server
  useEffect(() => {
    fetchHouses();
  }, []);

  async function fetchHouses() {
    try {
      setIsLoading(true);
      const res = await connection.get('/');
      setHouseData([...housedata, res.data]);
    } catch (e) {
      console.log(`An error occured1: ${e}`);
    } finally {
      setIsLoading(false);
    }
  }
  return (
    <div className='mainpage'>
      {isLoading ? (
        <Loading />
      ) : (
        housedata.map((house) => (
          <div key={Date.now()} className='housecard'>
            <h3>{house.title}</h3>
            <img alt={house.title} src={house.picture} />
            <p>Price: {house.price}</p>
            <p>Size: {house.space}</p>
            <p>
              <span style={{ fontWeight: 'bold' }}>About:</span>
              {house.desc}
            </p>
          </div>
        ))
      )}
    </div>
  );
}

export default MainPage;
