import React from 'react';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Link, Navigate } from 'react-router-dom';
import { PasswordRequest } from '../redux/features/auth/authSlice';

function PassReminder() {
  const dispatch = useDispatch();
  const [reminderPath, setReminderPath] = useState('no');
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({});

  async function submitPassRequest(values) {
    try {
      await dispatch(PasswordRequest(values));
      console.log(values);
      return <Navigate to='/' />;
    } catch (error) {
      console.log(`Password Request Failed: ${error}`);
    }
  }

  return (
    <div className='container'>
      {reminderPath === 'no' ? (
        <form className='authForm' onSubmit={handleSubmit(submitPassRequest)}>
          <div style={{ textAlign: 'center' }}>
            <h1>Восстановление пароля</h1>
            <h3>Пожалуйста введите данные</h3>
          </div>
          <label htmlFor='email'>Email</label>
          <input
            name='email'
            id='email'
            className='input'
            type='email'
            placeholder='email@email.com'
            {...register('email', {
              required: 'Пожалуйста введите email',
              pattern: {
                value: /\S+@\S+\.\S+/,
                message: 'Пожалуйста введите корректный email',
              },
            })}
          />
          <div style={{ marginTop: '15px' }}>
            <button className='filledBtn' type='submit'>
              Восстановить пароль
            </button>
            <ul className='errorList'>
              <li>{errors.email?.message}</li>
            </ul>
          </div>
        </form>
      ) : (
        <p></p>
      )}
      <Link className='additionalLinks'>Don't have an account yet?</Link>
    </div>
  );
}

export default PassReminder;
