import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import LoginForm from './components/login';
import Modal from './components/UI/modal/modal';
import MainPage from './pages/main';
import Registration from './pages/registration';
import Header from './components/header';
import Card from './pages/card';
import { useDispatch, useSelector } from 'react-redux';
import { getHouses } from './redux/features/houses/houseSlice';
import './App.css';

function App() {
  const [modal, setModal] = useState(false); // modal window
  const [isLoading, setIsLoading] = useState(false); // loading rotation
  const [housedata, setHouseData] = useState([]);
  const [carddata, setCardData] = useState({});
  const dispatch = useDispatch();
  const { houses } = useSelector((state) => state.houses);

  //connecting to a server
  useEffect(() => {
    dispatch(getHouses());
  }, []);

  return (
    <BrowserRouter>
      <Header setModal={setModal} />
      <Modal visible={modal} setVisible={setModal}>
        <LoginForm />
      </Modal>
      <Routes>
        <Route
          path='/'
          element={
            <MainPage
              loading={isLoading}
              housedata={housedata}
              setHouses={setHouseData}
              cardData={setCardData}
            />
          }
        />
        <Route path='/registration' element={<Registration />} />
        <Route
          path={'/' + carddata._id}
          element={<Card cardData={carddata} loading={setIsLoading} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
