import React, { useState } from 'react';
import residence from '../residence.svg';
import house from '../house.svg';
import hotel from '../hotel.svg';
import cl from './filter.module.css';
import { useDispatch } from 'react-redux';
import {
  searchQuery,
  chooseHouseType,
  chooseHousePrice,
  chooseHouseSpace,
} from '../../../redux/features/houses/houseSlice';

function Filters() {
  const [filter, setFilter] = useState({
    type: '', // search, space, price, quartertype
    subtype: '', // less, more
    query: '',
  });
  const dispatch = useDispatch();
  function pickFilter(e) {
    if (e.target.id === 'search') {
      dispatch(searchQuery(e.target.value));
    }
    if (e.target.id === 'pricemin')
      dispatch(chooseHousePrice({ query: e.target.value, subtype: 'more' }));
    if (e.target.id === 'pricemax')
      dispatch(chooseHousePrice({ query: e.target.value, subtype: 'less' }));
  }
  return (
    <div className={cl.filters}>
      <div className='fhat'>ПОИСК & ФИЛЬТРЫ</div>
      <div className={cl.search}>
        <span>Поиск</span>
        <input
          id='search'
          placeholder='Поиск'
          className='searchInp'
          onChange={pickFilter}
        ></input>
      </div>
      <span>Цена</span>
      <div className='prices'>
        <div className='rows'>
          <label htmlFor='pricemin'>От:</label>
          <input
            id='pricemin'
            min={0}
            className='minmax'
            onChange={pickFilter}
          />
        </div>
        <div className='rows'>
          <label htmlFor='pricemax'>До:</label>
          <input id='pricemax' className='minmax' onChange={pickFilter} />
        </div>
      </div>
      <span>Площадь</span>
      <div className='spaces'>
        <div className='rows'>
          <label htmlFor='spacemin'>От:</label>
          <input
            id='spacemin'
            min={0}
            className='minmax'
            onChange={pickFilter}
          />
        </div>
        <div className='rows'>
          <label htmlFor='spacemax'>До:</label>
          <input id='spacemax' className='minmax' onChange={pickFilter} />
        </div>
      </div>
      <div className={cl.types}>
        <label>Тип жилья</label>
        <form>
          <label>
            <input type='radio' name='flat' value='flat' />
            <img
              src={residence}
              title='Квартиры'
              className={cl.housetype}
              onChange={(e) =>
                setFilter({ type: 'quartertype', query: 'flat' })
              }
            />
            <span>Квартиры</span>
          </label>
          <label>
            <input type='radio' name='estate' value='estate' />
            <img src={house} title='Дома' className={cl.housetype} />
            <span>Дома</span>
          </label>
          <label>
            <input type='radio' name='hotel' value='hotel' />
            <img src={hotel} title='Гостинцы' className={cl.housetype} />
            <span>Гостиницы</span>
          </label>
        </form>
      </div>
    </div>
  );
}

export default Filters;
