import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import server from '../../../utilites/connection';

const URI = '/';

const initialState = {
  houses: {
    items: [],
    status: null,
    maxprice: 0,
    maxspace: 0,
  },
  houseInfo: {
    title: '',
    price: 0,
    space: 0,
    address: '',
    desc: '',
    houseType: '',
    picture: '',
    googleurl: '',
    status: null,
  },
  filters: {
    subtype: '',
    query: 0,
  },
};

export const getHouses = createAsyncThunk('houses/getHouses', async () => {
  const res = await server.get(URI);
  return res.data;
});

export const houseSlice = createSlice({
  name: 'houses',
  initialState,
  reducers: {
    searchQuery: (state, action) => {
      console.log('Search query:', action.payload);
      [...state.houses.items] = state.houses.items.title.includes(
        action.payload
      );
    },
    chooseHouseType: (state, action) => {
      [...state.houses.items] = state.houses.items.filter(
        (house) => house.type === action.payload
      );
    },
    chooseHousePrice: (state, action) => {
      console.log(action.payload);
      if (action.payload.subtype === 'less')
        [...state.houses.items] = state.houses.items.filter(
          (house) => house.price < action.payload.query
        );
      if (action.payload.subtype === 'more')
        [...state.houses.items] = state.houses.items.filter(
          (house) => house.price > action.payload.query
        );
    },
    chooseHouseSpace: (state, action) => {
      if (action.payload.subtype === 'less')
        state.houses.items = state.houses.items.filter(
          (house) => house.space < action.payload
        );
      if (action.payload.subtype === 'more')
        state.houses.items = state.houses.items.filter(
          (house) => house.space > action.payload
        );
    },
  },
  extraReducers: {
    [getHouses.pending]: (state) => {
      state.houses.items = [];
      state.houses.status = 'loading';
    },
    [getHouses.fulfilled]: (state, action) => {
      state.houses.items = action.payload;
      // get Maximum houses price
      const priceMax = action.payload.reduce((acc, curr) =>
        acc.price > curr.price ? acc : curr
      );
      state.houses.maxprice = priceMax.price;

      // get Maximum houses space
      const spaceMax = action.payload.reduce((acc, curr) =>
        acc.space > curr.space ? acc : curr
      );
      state.houses.maxspace = spaceMax.space;

      console.log(state.houses.items.maxprice, state.houses.items.maxspace);
      state.houses.status = 'complete';
    },
    [getHouses.rejected]: (state) => {
      state.houses.items = [];
      state.houses.status = 'error';
    },
  },
});

export const {
  searchQuery,
  chooseHouseType,
  chooseHouseSpace,
  chooseHousePrice,
} = houseSlice.actions;
export default houseSlice.reducer;
