import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch, useSelector } from 'react-redux';
import { isAuthorized, UpdateUser } from '../redux/features/auth/authSlice';
import { Navigate } from 'react-router-dom';
import userIcon from '../components/UI/user.png';
import Loading from '../components/UI/loading/loading';

function ChangeData({ user }) {
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(true);
  const isAuth = useSelector(isAuthorized);

  setTimeout(() => {
    setLoading(false);
  }, 200);

  const {
    register,
    handleSubmit,
    watch,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      name: user.name,
      email: user.email,
      phone: user.phone,
      password: '',
    },
  });

  async function submitUpdate(values) {
    try {
      const res = await dispatch(UpdateUser(values));
      if (!res.payload) {
        console.log('Error');
      }
    } catch (error) {
      console.log(error);
    }
  }
  return (
    <div className='container'>
      {loading ? (
        <Loading />
      ) : isAuth ? (
        <form className='authForm' onSubmit={handleSubmit(submitUpdate)}>
          <h1>Личный кабинет</h1>
          <h3>Изменить данные</h3>
          <img src={userIcon} className='userIcon' alt='You' />
          <label htmlFor='userName'>Ваше имя</label>
          <input
            name='userName'
            id='userName'
            {...register('name', {
              required: 'Пожалуйста, введите имя',
              maxLength: {
                value: 2,
                message: 'Имя не должно быть менее 2 символов',
              },
            })}
          />
          <label htmlFor='email'>Email</label>
          <input
            name='email'
            id='email'
            {...register('email', {
              required: 'Пожалуйста, введите email',
              pattern: {
                value: /\S+@\S+\.\S+/,
                message: 'Пожалуйста, введите корректный email',
              },
            })}
          />
          <label htmlFor='phone'>Телефон</label>
          <input
            name='phone'
            id='phone'
            {...register('phone', {
              required: 'Пожалуйста введите телефон',
              pattern: {
                value:
                  /^[\+]?[(]?[0-9]{3}[)]?[-\s\.]?[0-9]{3}[-\s\.]?[0-9]{4,6}$/,
                message: 'Пожалуйста введите корркетный номер телефона',
              },
            })}
          />
          <label htmlFor='password'>Пароль</label>
          <input
            name='password'
            id='password'
            {...register('password', {
              required: 'Пожалуйста, введите пароль',
              minLength: {
                value: 8,
                message: 'Пароль должен состоять из 8 и более символов',
              },
            })}
          />
          <label htmlFor='repeat_password'>Повторите пароль</label>
          <input
            name='repeat_password'
            id='repeat_password'
            {...register('repeat_password', {
              required: 'Пожалуйста повторите пароль',
              minLength: {
                value: 8,
                message: 'Пароль должен состоять из 8 и более символов',
              },
              validate: (val) => {
                if (watch('password') !== val) {
                  return 'Пароли не совпадают';
                }
              },
            })}
          />
          <br />
          <button className='filledBtn' type='submit'>
            Принять
          </button>
          <ul className='errorList'>
            <li>{errors.email?.message}</li>
            <li>{errors.password?.message}</li>
            <li>{errors.phone?.message}</li>
            <li>{errors.userName?.message}</li>
            <li>{errors.password_repeat?.message}</li>
          </ul>
        </form>
      ) : (
        <Navigate to='/' />
      )}
    </div>
  );
}

export default ChangeData;
