import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useForm } from 'react-hook-form';
import { isAuthorized } from '../redux/features/auth/authSlice';
import '../App.css';
import cl from './registration.module.css';

function Registration() {
  const dispatch = useDispatch();
  const [validationErrors, setValidationErrors] = useState([]);
  const isAuth = useSelector(isAuthorized);
  const {
    register,
    handleSubmit,
    setError,
    watch,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      userName: '',
      email: '',
      phone: '',
      password: '',
    },
    mode: 'onChange',
  });

  async function submitRegister(values) {
    const res = await dispatch(() => {});
  }

  // ДИМА, ДОБАВЬ В СТРЕЛОЧНУЮ ФУНКЦИЮ ФУНКЦИЮ И ТАМ ПРОВЕРЯЙ, А ЕСЛИ ОК ТО ПУШ ЗНАЧЕНИЕ

  return (
    <div className='container'>
      <form className='registration' onSubmit={handleSubmit(submitRegister)}>
        <div>
          <h1>Registration</h1>
          <h3>Welcome to Rent A House</h3>
        </div>
        <label htmlFor='name'>Your name</label>
        <input
          className='input'
          type='text'
          name='name'
          placeholder='John Doe'
          {...register('userName', {
            required: 'Please fill in your name',
            minLength: {
              value: 2,
              message: "Your name shouldn't be less than 2 symbols",
            },
          })}
        />
        <label htmlFor='email'>Your email</label>
        <input
          className='input'
          type='email'
          name='email'
          placeholder='email@email.com'
          {...register('email', {
            required: 'Please enter your email',
            pattern: {
              value: /\S+@\S+\.\S+/,
              message: 'Please enter a valid email',
            },
          })}
        />
        <label htmlFor='phone'>Your phone number</label>
        <input
          className='input'
          type='tel'
          name='phone'
          placeholder='88005553535'
          {...register('phone', {
            required: 'Please enter your phone number',
            pattern: {
              value:
                /^[\+]?[(]?[0-9]{3}[)]?[-\s\.]?[0-9]{3}[-\s\.]?[0-9]{4,6}$/,
              message: 'Please enter a valid phone number',
            },
          })}
        />
        <label htmlFor='password'>Password</label>
        <input
          className='input'
          type='password'
          name='phone'
          placeholder='Password'
          {...register('password', {
            required: 'Please fill in your password', minLength: {
              value: 8,
              message: 'Password should be 8 symbols length',
          }})}
        />
        <label htmlFor='repeatpass'>Repeat Password</label>
        <input
          className='input'
          type='password'
          name='password_repeat'
          placeholder='Repeat password'
          {...register('repeat_password', {
            required: 'Please repeat your password', minLength: {
              value: 8,
              message: 'Password should be 8 symbols length',
            }, validate: (val) => {
              if (watch('password') !== val) {
                return 'Passwords do not match'
              }
            }
  })}
        />
        <div style={{ marginTop: '15px' }}>
          <button className='filledBtn' type='submit'>
            Submit Registration
          </button>
        </div>
        <div className={cl.errorlist}>
          {validationErrors.length !== 0 ? (
            validationErrors.map((e) => <p key={e + 1}>{e}</p>)
          ) : (
            <span></span>
          )}
        </div>
      </form>
    </div>
  );
}

export default Registration;
