import axios from 'axios';
import React, { useRef, useState } from 'react';
import '../App.css';
import cl from './registration.module.css';

function Registration({ users, createUsers }) {
  const [user, setUser] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
  });
  const [validated, setValidated] = useState(false);
  const [validationErrors, setValidationErrors] = useState([]);
  const [password, setPassword] = useState('');

  /*const nameIn = useRef();
  const emailIn = useRef();
  const phoneIn = useRef();
  const passIn = useRef();
  const repIn = useRef();
  */

    // ДИМА, ПОЧИНИ ВАЛИДАЦИЮ!
  function Validation(e) {
    setValidationErrors([]);
    // Checking Name
    if (e.target.id === 'name' && e.target.value) {
      e.target.className = 'input';
      setUser({ ...user, name: e.target.value });
    } else {
      setValidationErrors([...validationErrors, 'Please add your name']); // ДОБАВЬ И УДАЛЕНИЕ ИЗ МАССИВА
      e.target.className = 'error';
    }
    // Checking Email
    if (e.target.id === 'email' && e.target.value) {
      // if it is email
      if (/\S+@\S+\.\S+/.test(e.target.value) === true) {
        const onCheck = e.target.value;
        checkEmail(onCheck);

        async function checkEmail(email) {
          try {
            const res = await axios.get('http://localhost:8000/', {
              email: email,
            }); // ВПИШИ ТЕЛО запроса
            if (res === 'ok') {
              // Если ответ = ОК, тогда запиши значение
              setUser({ ...user, email: e.target.value });
            } else {
              setValidationErrors([
                ...validationErrors,
                'User with this email already exists',
              ]);
              e.target.className = 'error';
            }
          } catch (e) {
            setValidationErrors([...validationErrors, e]);
          }
        }
      }
    } else {
      setValidationErrors([...validationErrors, 'Please add your email']);
      e.target.className = 'error';
    }
    // Checking Phone
    if (e.target.id === 'phone' && e.target.value) {
      // if it is phone
      if (/\N + \N\./.test(e.target.value) === true) {
        const onCheck = e.target.value;
        checkPhone(onCheck);

        async function checkPhone(phone) {
          try {
            const res = await axios.get('http://localhost:8000/', {
              phone: phone,
            }); // ВПИШИ ТЕЛО запроса
            if (res === 'ok') {
              setUser({ ...user, phone: e.target.value });
            } else {
              setValidationErrors([
                ...validationErrors,
                'User with this phone already exists',
              ]);
              e.target.className = 'error';
            }
          } catch (e) {
            setValidationErrors([...validationErrors, e]);
          }
        }
      }
    } else {
      setValidationErrors([...validationErrors, 'Please add your phone']);
      e.target.className = 'error';
    }
    // Checking passwords
    if (e.target.id === 'password' && e.target.value) {
      setPassword(e.target.value);
    } else {
      setValidationErrors([...validationErrors, 'Please add a password']);
      e.target.className = 'error';
    }

    if (e.target.id === 'repeatpass' && e.target.value) {
      if (e.target.value === password) {
        e.target.className = 'input';
        setUser({ ...user, password: e.target.value });
      } else {
        setValidationErrors([...validationErrors, `Passwords do not match`]);
        e.target.className = 'error';
      }
    } else {
      setValidationErrors([...validationErrors, 'Please repeat your password']);
      e.target.className = 'error';
    }
    if (validationErrors.length === 0) {
      setValidated(true);
    }
  }

  function submit(e) {
    e.preventDefault();

    if (validated) {
      const newUser = { ...user };
      createUsers(newUser);
    }
  }
  // ДИМА, ДОБАВЬ В СТРЕЛОЧНУЮ ФУНКЦИЮ ФУНКЦИЮ И ТАМ ПРОВЕРЯЙ, А ЕСЛИ ОК ТО ПУШ ЗНАЧЕНИЕ

  return (
    <div className='container'>
      <h1>Registration</h1>
      <form className='registration'>
        <label htmlFor='name'>Your name</label>
        <input
          className='input'
          id='name'
          type='text'
          name='name'
          placeholder='John Doe'
          onChange={Validation}
          required
        />
        <label htmlFor='email'>Your email</label>
        <input
          className='input'
          id='email'
          type='email'
          name='email'
          placeholder='email@email.com'
          onChange={Validation}
          required
        />
        <label htmlFor='phone'>Your phone number</label>
        <input
          className='input'
          id='phone'
          type='text'
          name='phone'
          placeholder='8 800 5553535'
          onChange={Validation}
          required
        />
        <label htmlFor='password'>Password</label>
        <input
          className='input'
          id='password'
          type='password'
          name='phone'
          placeholder='Password'
          minLength={8}
          onChange={Validation}
          required
        />
        <label htmlFor='repeatpass'>Repeat Password</label>
        <input
          className='input'
          id='repeatpass'
          type='password'
          name='phone'
          placeholder='Password'
          onChange={Validation}
          minLength={8}
          required
        />
        <div>
          <button onClick={submit}>Submit Registration</button>
        </div>
        <div id='errors'>
          <ul className={cl.errorlist}>
            {validationErrors.length !== 0 ? (
              validationErrors.map((e) => <li>{e}</li>)
            ) : (
              <span></span>
            )}
          </ul>
        </div>
      </form>
    </div>
  );
}

export default Registration;
