import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import server from '../../../utilites/connection';

const URI = '/';
const filterURI = '/filter';

const initialState = {
  houses: {
    items: [],
    status: null,
    maxprice: 0,
    maxspace: 0,
  },
  houseInfo: {
    title: '',
    price: 0,
    space: 0,
    address: '',
    desc: '',
    houseType: '',
    picture: '',
    googleurl: '',
    status: null,
  },
  filters: {
    subtype: '',
    query: 0,
  },
};

export const getHouses = createAsyncThunk('houses/getHouses', async () => {
  const res = await server.get(URI);
  return res.data;
});

export const filterHouses = createAsyncThunk(
  'houses/filterHouses',
  async (params) => {
    const res = await server.get(filterURI, {
      title: params.searchQuery,
      maxPrice: params.maxPrice,
      minPrice: params.minPrice,
      maxSpace: params.maxSpace,
      minSpace: params,
      minSpace,
      houseType: '',
    });
    return res.data;
  }
);

export const houseSlice = createSlice({
  name: 'houses',
  initialState,
  reducers: {
    applyFilters: (state, action) => {
      console.log('Вы запросили', action.payload);
      [...state.houses.items] = state.houses.items.filter((query) => {
        return (
          query.title
            .toLowerCase()
            .includes(action.payload.searchQuery.toLowerCase()) &&
          query.price >= action.payload.priceMin &&
          query.price <= action.payload.priceMax &&
          query.space >= action.payload.spaceMin &&
          query.space <= action.payload.spaceMax
        );
      });
    },
  },
  extraReducers: {
    [getHouses.pending]: (state) => {
      state.houses.items = [];
      state.houses.status = 'loading';
    },
    [getHouses.fulfilled]: (state, action) => {
      state.houses.items = action.payload;
      // get Maximum houses price
      const priceMax = action.payload.reduce((acc, curr) =>
        acc.price > curr.price ? acc : curr
      );
      state.houses.maxprice = priceMax.price;

      // get Maximum houses space
      const spaceMax = action.payload.reduce((acc, curr) =>
        acc.space > curr.space ? acc : curr
      );
      state.houses.maxspace = spaceMax.space;

      state.houses.status = 'complete';
    },
    [getHouses.rejected]: (state) => {
      state.houses.items = [];
      state.houses.status = 'error';
    },
  },
});

export const { applyFilters } = houseSlice.actions;
export default houseSlice.reducer;
