import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import LoginForm from './components/login';
import Modal from './components/UI/modal/modal';
import MainPage from './pages/main';
import Registration from './pages/registration';

function App() {
  const [modal, setModal] = useState(false); // modal window
  const [userdata, setUserData] = useState({
    id: '',
    name: '',
    email: '',
    phone: '',
    password: '',
  });
  const [housedata, setHouseData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  //connecting to a server
  useEffect(() => {
    fetchHouses();
  }, []);

  async function fetchHouses() {
    try {
      setIsLoading(true);
      const res = await axios.get('http://localhost:8000/');
      setHouseData([...housedata, res.data]);
    } catch (e) {
      console.log(`An error occured: ${e}`);
    }
  }

  return (
    <BrowserRouter>
      <div className='header'>
        <div className='logo'>LOGO</div>
        <div className='menu'>
          <Link className='links' to='/'>
            На главную
          </Link>
          <Link className='links' to='/registration'>
            Регистрация
          </Link>
          <button onClick={() => setModal(true)}>Login</button>
        </div>
      </div>

      <Modal visible={modal} setVisible={setModal}>
        <LoginForm />
      </Modal>
      <Routes>
        <Route path='/' element={<MainPage housedata={housedata} />} />
        <Route
          path='/registration'
          element={<Registration users={userdata} createUsers={setUserData} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
