import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getHouses } from '../redux/features/houses/houseSlice';
import { isAuthorized } from '../redux/features/auth/authSlice';
import Loading from '../components/UI/loading/loading';
import Filters from '../components/UI/filters/filters';
import HouseCard from '../components/UI/housecard/housecard';

function MainPage() {
  const dispatch = useDispatch();
  const { houses } = useSelector((state) => state.houses);
  const isAuth = useSelector(isAuthorized);
  const [pages, setPages] = useState([]);
  const [filter, setFilter] = useState({
    type: '', // search, space, price, quartertype
    subtype: '', // less, more
    query: '',
  });

  const isLoading = houses.status === 'loading';
  const isError = houses.status === 'error';

  // all filters
  function FilterObjects() {
    if (filter.type === 'search') {
      houses.filter(
        () => filter.query.tolowercase() === houses.title.tolowercase()
      );
    }
    if (filter.type === 'space') {
      if (filter.subtype === 'less') {
        houses.filter(() => filter.query > houses.space);
      }
      if (filter.subtype === 'more') {
        houses.filter(() => filter.query < houses.space);
      }
    }
    if (filter.type === 'price') {
      if (filter.subtype === 'less') {
        houses.filter(() => filter.query > houses.price);
      }
      if (filter.subtype === 'more') {
        houses.filter(() => filter.query < houses.price);
      }
    }
    if (filter.type === 'quartertype') {
      const type = filter.query;
      houses.filter(() => filter.query === houses.type);
    }
  }

  //connecting to a server
  useEffect(() => {
    loadFunctions();
  }, []);

  //changing filter
  useEffect(() => {
    FilterObjects();
  }, [setFilter]);

  async function loadFunctions() {
    try {
      const res = await dispatch(getHouses());
      for (let i = 0; i < res.payload.length / 6; i++) {
        pages.push(i + 1);
      }
      console.log('pages', pages, pages.length);
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div className='mainpage'>
      <Filters setFilter={setFilter} />
      {isError || houses.length === 0 ? (
        <p>Ничего не найдено, попробуйте другой запрос.</p>
      ) : isLoading ? (
        <Loading />
      ) : (
        <div className='cards'>
          {isAuth
            ? houses.items.map((house) => (
                <HouseCard house={house} key={house.title} />
              ))
            : houses.items.map(
                (house, index) =>
                  index < 6 && <HouseCard house={house} key={house.title} />
              )}
        </div>
      )}
      <div>
        {pages.length !== 0 ? (
          <span>хуй</span>
        ) : (
          <div>
            {pages.forEach((page) => {
              <span>страницца</span>;
            })}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainPage;
