import React from 'react';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { PasswordRequest, KeyCheck } from '../redux/features/auth/authSlice';

function PassReminder() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  // no, requested, approved
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({});

  async function submitPassRequest(values) {
    try {
      const res = await dispatch(PasswordRequest(values));
      if (res.status === 200) {
        navigate(`/keycheck:id:`, { replace: true });
      }
    } catch (error) {
      console.log(`Password Request Failed: ${error}`);
    }
  }

  async function submitKeyheck(values) {
    try {
      const res = await dispatch(KeyCheck(values));
      if (res.status === 200) {
        setUserId(res.data.id);
        setReminderPath('approved');
      }
    } catch (error) {
      console.log(`KeyCheckFailed: ${error}`);
    }
  }

  async function submitNewPassword(values) {}

  return (
    <div className='container'>
      <form className='authForm' onSubmit={handleSubmit(submitPassRequest)}>
        <div style={{ textAlign: 'center' }}>
          <h1>Восстановление пароля</h1>
          <h3>Пожалуйста введите данные</h3>
          <p>Обязательно проверьте правильность ввода данных</p>
        </div>
        <label htmlFor='email'>Email</label>
        <input
          name='email'
          id='email'
          className='input'
          type='email'
          placeholder='email@email.com'
          {...register('email', {
            required: 'Пожалуйста введите email',
            pattern: {
              value: /\S+@\S+\.\S+/,
              message: 'Пожалуйста введите корректный email',
            },
          })}
        />
        <div style={{ marginTop: '15px' }}>
          <button className='filledBtn' type='submit'>
            Восстановить пароль
          </button>
          <ul className='errorList'>
            <li>{errors.email?.message}</li>
          </ul>
        </div>
      </form>
    </div>
  );
}

export default PassReminder;
