import React, { useState, useEffect, Children, cloneElement } from 'react';
import left from '../left.png';
import right from '../right.png';
import cl from './slider.module.css';

function Slider({ children }) {
  const [pages, setPages] = useState([]);
  const [offset, setOffset] = useState(0);
  const slideWidth = 125;
  useEffect(() => {
    setPages(
      Children.map(children, (child) => {
        return cloneElement(child, {
          style: {
            height: '100%',
            maxWidth: `${slideWidth}px`,
            minWidth: `${slideWidth}px`,
          },
        });
      })
    );
  }, []);
  function moveLeft() {
    setOffset((currentOffset) => {
      const newOffset = currentOffset + 380;

      return Math.min(newOffset, 0);
    });
  }
  function moveRight() {
    setOffset((currentOffset) => {
      const newOffset = currentOffset - 380;

      const maxOffset = -(slideWidth * (pages.length - 1));

      return Math.max(newOffset, maxOffset / 2);
    });
  }
  return (
    <div className={cl.slider_container}>
      <img src={left} className={cl.navigation} onClick={moveLeft} />
      <div className={cl.slider_window}>
        <div
          className={cl.slider_items}
          style={{ transform: `translateX(${offset}px)` }}
        >
          {pages}
        </div>
      </div>
      <img src={right} className={cl.navigation} onClick={moveRight} />
    </div>
  );
}

export default Slider;
